-- ----------------------------
-- Procedure structure for PROC_QUERY_PARTS
-- ----------------------------
DROP PROCEDURE IF EXISTS `PROC_QUERY_PARTS`;
delimiter ;;
CREATE PROCEDURE `ware`.`PROC_QUERY_PARTS`(IN `data` varchar(20000))
BEGIN
	DECLARE _key varchar(50);
	SET     _key = JSON_UNQUOTE(JSON_EXTRACT(data,'$.key'));

	select id as `key`, t.*  from tab_parts  t where 
		part_number LIKE CONCAT('%', _key, '%') or
		part_desc   LIKE CONCAT('%', _key, '%') or
		org_country   LIKE CONCAT('%', _key, '%') or
		brand   LIKE CONCAT('%', _key, '%') or
		CONVERT(part_id, CHAR) = _key;
END
;;
delimiter ;

-- ----------------------------
-- Procedure structure for PROC_QUERY_TABLE
-- ----------------------------
DROP PROCEDURE IF EXISTS `PROC_QUERY_TABLE`;
delimiter ;;
CREATE PROCEDURE `ware`.`PROC_QUERY_TABLE`(IN `data` varchar(20000))
BEGIN
	SET SESSION TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;
	SET SESSION group_concat_max_len = 1000000;
	
	SET @t1 = JSON_UNQUOTE(JSON_EXTRACT(data, '$.table'));
	SET @t2 = JSON_UNQUOTE(JSON_EXTRACT(data, '$.subTable'));
	SET @ref = JSON_UNQUOTE(JSON_EXTRACT(data, '$.ref'));

  SET @column_sql = CONCAT('SELECT GROUP_CONCAT(CONCAT("CONCAT(\\\'", COLUMN_NAME, "\\\: \\\'", ", t2.", COLUMN_NAME, "),\\\'#\\\'")) INTO @columns FROM information_schema.COLUMNS WHERE TABLE_NAME = "', @t2, '"');
	PREPARE stmt1 FROM @column_sql;
  EXECUTE stmt1;
	
	SET @row_number = 0;
	SET @sql = CONCAT(
			'SELECT (@row_number := @row_number + 1) AS `key`, t1.*, GROUP_CONCAT(', @columns, ' SEPARATOR ", ") AS sub ',
			'FROM ', @t1, ' AS t1 ',
			'LEFT JOIN ', @t2, ' AS t2 ',
			'ON t1.', @ref, ' = t2.', @ref, ' ',
			'GROUP BY t1.', @ref
	);
	
-- 	call DEBUG_MSG(@sql);
	
	PREPARE stmt2 FROM @sql;
	EXECUTE stmt2;
	DEALLOCATE PREPARE stmt2;

END
;;
